mapper.update:
update `logininfo`
set username = #{username},
  password = #{password},
  state = #{state},
  userType = #{userType}
where id = #{id}

mapper.select:
select id, username, password, state, userType
from `logininfo`
where id = #{id}

 select id, username, password, state, userType
from `logininfo`
where username = #{username}

select id, username, password, state, userType
from `logininfo`
where username = #{username} and password=#{password} and userType=#{userType}
